/* eslint-disable @typescript-eslint/naming-convention */
import { JsonRpcResponseStruct } from '@metamask/utils';

import { flushPromises } from '../../../../test/lib/timer-helpers';
import { mockBridgeQuotes } from '../../../../test/data/confirmations/contract-interaction';
import { createDappSwapMiddleware } from './dapp-swap-middleware';
import { DappSwapMiddlewareRequest } from './dapp-swap-util';

const REQUEST_MOCK = {
  params: [],
  id: '1234567',
  jsonrpc: '2.0' as const,
  origin: 'test.com',
  networkClientId: 'networkClientId',
};

let fetchQuotes = jest.fn();
const setDappSwapComparisonData = jest.fn();
const getNetworkConfigurationByNetworkClientId = jest.fn();

const createMiddleware = (
  args: {
    // eslint-disable-next-line @typescript-eslint/naming-convention
    dappSwapMetricsFlag?: {
      enabled: boolean;
      bridge_quote_fees: number;
      origins: string[];
    };
  } = {},
) => {
  const middlewareFunction = createDappSwapMiddleware({
    fetchQuotes,
    setDappSwapComparisonData,
    getNetworkConfigurationByNetworkClientId,
    // eslint-disable-next-line @typescript-eslint/naming-convention
    dappSwapMetricsFlag: {
      enabled: true,
      bridge_quote_fees: 250,
      origins: ['https://metamask.github.io'],
    },
    ...args,
  });
  return { middlewareFunction };
};

describe('DappSwapMiddleware', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  it('gets the network configuration for the request networkClientId', async () => {
    const { middlewareFunction } = createMiddleware();

    const req = {
      ...REQUEST_MOCK,
      method: 'eth_sendTransaction',
    };

    await middlewareFunction(
      req,
      { ...JsonRpcResponseStruct.TYPE },
      () => undefined,
    );

    await flushPromises();
  });

  it('does not fetch quotes if dapp swap is not enabled', async () => {
    fetchQuotes.mockReturnValueOnce(mockBridgeQuotes);
    const { middlewareFunction } = createMiddleware({
      // eslint-disable-next-line @typescript-eslint/naming-convention
      dappSwapMetricsFlag: {
        enabled: false,
        bridge_quote_fees: 250,
        origins: ['https://metamask.github.io'],
      },
    });

    const req = {
      ...REQUEST_MOCK,
      method: 'eth_sendTransaction',
      origin: 'https://metamask.github.io',
      securityAlertResponse: {
        securityAlertId: '1234567',
      },
      params: [
        {
          data: '',
          from: '0x12312312312312',
          chainId: '1',
          calls: [],
        },
      ],
      networkClientId: 'networkClientId',
    };

    await middlewareFunction(
      req as unknown as DappSwapMiddlewareRequest<(string | { to: string })[]>,
      { ...JsonRpcResponseStruct.TYPE },
      () => undefined,
    );

    await flushPromises();

    expect(fetchQuotes).not.toHaveBeenCalled();
  });

  it('for correct origin, fetches quotes and sets swap quotes', async () => {
    fetchQuotes.mockReturnValueOnce(mockBridgeQuotes);
    getNetworkConfigurationByNetworkClientId.mockReturnValueOnce({
      chainId: '0x1',
      rpcEndpoints: [],
    });
    const { middlewareFunction } = createMiddleware();

    const req = {
      ...REQUEST_MOCK,
      method: 'eth_sendTransaction',
      origin: 'https://metamask.github.io',
      securityAlertResponse: {
        securityAlertId: '1234567',
      },
      params: [
        {
          data: '',
          from: '0x12312312312312',
          calls: [],
        },
      ],
      networkClientId: 'networkClientId',
    };

    await middlewareFunction(
      req as unknown as DappSwapMiddlewareRequest<(string | { to: string })[]>,
      { ...JsonRpcResponseStruct.TYPE },
      () => undefined,
    );

    await flushPromises();

    expect(fetchQuotes).toHaveBeenCalledWith({
      destChainId: '0x1',
      destTokenAddress: '0xaf88d065e77c8cc2239327c5edb3a432268e5831',
      fee: 250,
      gasIncluded: false,
      gasIncluded7702: false,
      srcChainId: '0x1',
      srcTokenAddress: '0x0000000000000000000000000000000000000000',
      srcTokenAmount: '0x5af3107a4000',
      walletAddress: '0x12312312312312',
    });
  });

  it('capture error and commands if quote fetching fails', async () => {
    fetchQuotes = jest.fn().mockRejectedValue(new Error('fail'));
    getNetworkConfigurationByNetworkClientId.mockReturnValueOnce({
      chainId: '0x1',
      rpcEndpoints: [],
    });
    const { middlewareFunction } = createMiddleware();

    const req = {
      ...REQUEST_MOCK,
      method: 'eth_sendTransaction',
      origin: 'https://metamask.github.io',
      securityAlertResponse: {
        securityAlertId: '1234567',
      },
      params: [
        {
          data: '',
          from: '0x12312312312312',
          calls: [],
        },
      ],
      networkClientId: 'networkClientId',
    };

    await middlewareFunction(
      req as unknown as DappSwapMiddlewareRequest<(string | { to: string })[]>,
      { ...JsonRpcResponseStruct.TYPE },
      () => undefined,
    );

    await flushPromises();

    expect(setDappSwapComparisonData).toHaveBeenCalledWith('1234567', {
      commands: '0x100604',
      error: 'Error fetching bridge quotes: fail',
    });
  });

  it('capture error if request is not valid batch request', async () => {
    fetchQuotes = jest.fn().mockRejectedValue(new Error('fail'));
    getNetworkConfigurationByNetworkClientId.mockReturnValueOnce({
      chainId: '0x1',
      rpcEndpoints: [],
    });
    const { middlewareFunction } = createMiddleware();

    const req = {
      ...REQUEST_MOCK,
      method: 'eth_sendTransaction',
      origin: 'https://metamask.github.io',
      securityAlertResponse: {
        securityAlertId: '1234567',
      },
      params: [
        {
          data: '',
          from: '0x12312312312312',
          calls: [
            {
              data: '',
            },
            { data: 'dummy_test_data' },
          ],
        },
      ],
      networkClientId: 'networkClientId',
    };

    await middlewareFunction(
      req as unknown as DappSwapMiddlewareRequest<(string | { to: string })[]>,
      { ...JsonRpcResponseStruct.TYPE },
      () => undefined,
    );

    await flushPromises();

    expect(setDappSwapComparisonData).toHaveBeenCalledWith('1234567', {
      commands: '0x100604',
      error:
        'Error fetching bridge quotes: Error getting data from swap: invalid batch transaction, invalid command',
    });
  });

  it('does not fetch quotes if origin is not in the list of allowed origins', async () => {
    fetchQuotes.mockReturnValueOnce(mockBridgeQuotes);
    const { middlewareFunction } = createMiddleware({
      // eslint-disable-next-line @typescript-eslint/naming-convention
      dappSwapMetricsFlag: {
        enabled: true,
        bridge_quote_fees: 250,
        origins: ['https://metamask.github.io'],
      },
    });

    const req = {
      ...REQUEST_MOCK,
      method: 'eth_sendTransaction',
      origin: 'https://test.com',
      securityAlertResponse: {
        securityAlertId: '1234567',
      },
      params: [
        {
          data: '',
          from: '0x12312312312312',
          chainId: '1',
          calls: [],
        },
      ],
      networkClientId: 'networkClientId',
    };

    await middlewareFunction(
      req as unknown as DappSwapMiddlewareRequest<(string | { to: string })[]>,
      { ...JsonRpcResponseStruct.TYPE },
      () => undefined,
    );

    await flushPromises();

    expect(fetchQuotes).not.toHaveBeenCalled();
  });
});
